mapper.select:
SELECT r.*, u.nickName hostName FROM roomInfo r
LEFT JOIN user u ON r.hostId = u.id
WHERE r.userCount = #{count}

SELECT r.*, u.nickName hostName FROM roomInfo r
LEFT JOIN user u ON r.hostId = u.id
WHERE r.level = #{level}

mapper.update:
UPDATE roomInfo 

	hostId = #{hostId},
	 awayId = #{awayId}, 
	level = #{level}, userCount = #{userCount}

WHERE id = #{id}
